```java
package com.coderq.houserent.service;

import com.coderq.houserent.domain.House;

/**
 * Created by Q on 2022/2/26.
 *
 * ä¸šåŠ¡å±‚
 * å®šä¹‰House[],ä¿å­˜Houseå¯¹è±¡
 * 1.å“åº”HouseViewçš„è°ƒç”¨
 * 2.å®Œæˆå¯¹æˆ¿å±‹ä¿¡æ¯çš„å¢åˆ æ”¹æŸ¥ï¼ˆcrudï¼‰
 */
public class HouseService {
    private House[] houses; //ä¿å­˜Houseå¯¹è±¡
    private int houseNums = 1; //è®°å½•å¤šå°‘ä¸ªæˆ¿å±‹ä¿¡æ¯
    private int idCounter = 1; //è®°å½•å½“å‰Idå¢åŠ åˆ°å“ª
    //æ„é€ å™¨åˆå§‹åŒ–æ•°æ®
    public HouseService(int size) {
        //new houses
        houses = new House[size]; //åˆ›å»ºHouseServiceå¯¹è±¡æ—¶ï¼ŒæŒ‡å®šæ•°ç»„å¤§å°
        //ä¼ªé€ æ•°æ®ï¼Œé…åˆæµ‹è¯•
        houses[0] = new House(1,"coderq","188","å®‰å¾½",500,"æœªå‡ºç§Ÿ");
    }

    //ä¿®æ”¹æˆ¿å±‹ä¿¡æ¯


    //findæ–¹æ³•,æŸ¥æ‰¾æˆ¿å±‹ä¿¡æ¯,è¿”å›Houseå¯¹è±¡æˆ–null
    public House findById(int findId){
        //å…ˆæ‰¾åˆ°è¦æŸ¥æ‰¾çš„æˆ¿å±‹å¯¹åº”çš„ä¸‹æ ‡
        for (int i = 0; i < houseNums; i++) {
            if (findId == houses[i].getId() ){ //è¦æŸ¥æ‰¾çš„æˆ¿å±‹ï¼ˆidï¼‰
                return houses[i];
            }
        }
        return null;
    }

    //delæ–¹æ³•,åˆ é™¤æˆ¿å±‹ä¿¡æ¯
    public boolean del(int delId){
        //å…ˆæ‰¾åˆ°è¦åˆ é™¤çš„æˆ¿å±‹å¯¹åº”çš„ä¸‹æ ‡
        int index = -1;
        for (int i = 0; i < houseNums; i++) {
            if (delId == houses[i].getId() ){ //è¦åˆ é™¤çš„æˆ¿å±‹ï¼ˆidï¼‰ï¼Œæ˜¯æ•°ç»„ä¸‹æ ‡ä¸ºiçš„å…ƒç´ 
                index = i;
            }
        }
        if (index == -1){
            //System.out.println("è¯¥æˆ¿å±‹ä¸å­˜åœ¨");
            return false;
        }
        //å¦‚æœæ‰¾åˆ°,æœ‰ç‚¹éš¾åº¦
        for (int i = index; i < houseNums - 1 ; i++) {
            houses[i] = houses[i+1];
        }
        houses[--houseNums] = null; //å½“å‰æˆ¿å±‹ä¿¡æ¯æœ€åä¸€ä¸ª è®¾ç½®null
        return true;
    }

    //addæ–¹æ³•ï¼Œæ·»åŠ æ–°å¯¹è±¡ï¼Œè¿”å›boolean
    public boolean add(House newHouse){
        //åˆ¤æ–­æ˜¯å¦è¿˜å¯ä»¥æ·»åŠ ï¼Œæ•°ç»„æ»¡å¦‚ä½•æ‰©å®¹ï¼Ÿ
        if(houseNums == houses.length){
            System.out.println("æ•°ç»„å·²æ»¡ï¼Œæ— æ³•æ·»åŠ ");
            return false;
        }
        //newHouseå¯¹è±¡åŠ å…¥åˆ°æ•°ç»„
        houses[houseNums++] = newHouse;
        //idè‡ªå¢æœºåˆ¶
        newHouse.setId(++idCounter);
        return true;
    }

    //listæ–¹æ³•ï¼Œè¿”å›houses
    public House[] list(){
        return houses;
    }
}
